mapper.select:
SELECT BRAND_NUMBER, BRAND_NAME, BRAND_CONTENTS
FROM MONTHLY_BRAND
WHERE SELLER_NUMBER = #{sellerNumber}

SELECT BRAND_NUMBER, BRAND_CONTENTS, BRAND_NAME, BRAND_REGISTER_DATE, SELLER_NUMBER
FROM MONTHLY_BRAND
WHERE BRAND_NUMBER =#{brandNumber}

mapper.update:
UPDATE MONTHLY_BRAND
SET BRAND_CONTENTS =#{brandContents}, BRAND_NAME = #{brandName}
WHERE SELLER_NUMBER =#{sellerNumber}
