mapper.update:
UPDATE MONTHLY_PRODUCT
SET PRODUCT_NAME = #{productName}, PRODUCT_PRICE = #{productPrice}, PRODUCT_AMOUNT=#{productAmount},
    PRODUCT_OPTION = #{productOption}, PRODUCT_CONTENTS=#{productContents}, PRODUCT_STATUS = #{productStatus}
WHERE PRODUCT_NUMBER = #{productNumber}

mapper.select:
SELECT PRODUCT_NUMBER, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_OPTION, PRODUCT_AMOUNT, PRODUCT_REGISTER_DATE, PRODUCT_STATUS, PRODUCT_CONTENTS, BRAND_NUMBER, BRAND_NAME FROM(SELECT ROWNUM AS RNUM,PRODUCT_NUMBER, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_OPTION, PRODUCT_AMOUNT, PRODUCT_REGISTER_DATE, PRODUCT_STATUS, PRODUCT_CONTENTS, BRAND_NUMBER, BRAND_NAME FROM( SELECT PRODUCT_NUMBER, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_OPTION, PRODUCT_AMOUNT, PRODUCT_REGISTER_DATE, PRODUCT_STATUS, PRODUCT_CONTENTS, B.BRAND_NUMBER, B.BRAND_NAME FROM MONTHLY_PRODUCT P JOIN MONTHLY_BRAND B ON P.BRAND_NUMBER = B.BRAND_NUMBER AND SELLER_NUMBER= #{sellerNumber} order by PRODUCT_NUMBER desc) S1
WHERE ROWNUM <= #{criteria.page}*#{criteria.amount}
)S2 WHERE RNUM > (#{criteria.page}-1)*#{criteria.amount}

SELECT COUNT(PRODUCT_NUMBER) FROM MONTHLY_PRODUCT WHERE BRAND_NUMBER IN (
    SELECT BRAND_NUMBER FROM MONTHLY_BRAND WHERE SELLER_NUMBER = #{sellerNumber}
)
